mapper.select:
SELECT B.* FROM ( SELECT A.*, ROWNUM RNUM FROM( SELECT PROD_ID, LPROD_NM, PROD_NAME, BUYER_NAME, PROD_COST, PROD_PRICE, PROD_MILEAGE
) A ) B
where rnum>=#{startRow} and rnum<=#{endRow}

mapper.update:
UPDATE prod SET PROD_NAME = #{prod_name,jdbcType=VARCHAR}, PROD_COST = #{prod_cost,jdbcType=NUMERIC}, PROD_PRICE = #{prod_price,jdbcType=NUMERIC}, PROD_SALE = #{prod_sale,jdbcType=NUMERIC}, PROD_OUTLINE = #{prod_outline,jdbcType=VARCHAR}, PROD_DETAIL = #{prod_detail,jdbcType=VARCHAR}, PROD_TOTALSTOCK = #{prod_totalstock,jdbcType=NUMERIC}, PROD_PROPERSTOCK= #{prod_properstock,jdbcType=NUMERIC}, PROD_SIZE = #{prod_size,jdbcType=VARCHAR}, PROD_COLOR = #{prod_color,jdbcType=VARCHAR}, PROD_DELIVERY = #{prod_delivery,jdbcType=VARCHAR}, PROD_UNIT = #{prod_unit,jdbcType=VARCHAR}, PROD_QTYIN = #{prod_qtyin,jdbcType=NUMERIC}, PROD_QTYSALE = #{prod_qtysale,jdbcType=NUMERIC}, PROD_MILEAGE = #{prod_mileage,jdbcType=NUMERIC}
, PROD_IMG = #{prod_img,jdbcType=VARCHAR}
WHERE PROD_ID = #{prod_id,jdbcType=VARCHAR}
